function CounFormat (count) {
  var countString = count.toString()
  var tmp = ''
  var result = ''
  if (countString.length >= 5) {
    tmp = countString.slice(-4, -3) === '0' ? '' : countString.slice(-4, -3)
    if (tmp.slice(tmp.length + 1, 1) === 0) {
      result = countString.slice(0, -4).concat('w')
